import java.io.*;
public class Main {
public static void main(String[] args) throws IOException {
BufferedReader inputReader = new BufferedReader(new FileReader(args[0]));
BufferedReader outputReader = new BufferedReader(new FileReader(args[1]));
int a = Integer.parseInt(inputReader.readLine());
int b = Integer.parseInt(inputReader.readLine());
int userResult = new Problem().add(a, b);
int expectedResult = Integer.parseInt(outputReader.readLine());
// For testing purposes let's fail it
if (userResult == 300) {
userResult++;
}
if (userResult == expectedResult) {
ResultWriter.Pass();
} else {
ResultWriter.Fail();
}
inputReader.close();
}
}

class Problem {
public int add(int a, int b) {
return a + b;
}
}

import java.io.BufferedWriter;
import java.io.FileWriter;
import java.io.IOException;
public class ResultWriter {
private static final String resultRelativePath = "result/result.txt";
private static final BufferedWriter writer;
static {
try {
writer = new BufferedWriter(new FileWriter(resultRelativePath, true));
} catch (IOException e) {
throw new RuntimeException(e);
}
}
public static void Pass() throws IOException {
writer.write("Pass\n");
writer.flush();
}
public static void Fail() throws IOException {
writer.write("Fail\n");
writer.flush();
}
}
